import React, { useState } from "react";
const App2 = () => {
  const [image, setImage] = useState("");
  const [url, setUrl] = useState("");
  const uploadImage = () => {
    const data = new FormData();
    data.append("file", image);
    data.append("upload_preset", "uploadpresetforreact");
    data.append("cloud_name", "dshhfnpgu");
    fetch("https://api.cloudinary.com/v1_1/dshhfnpgu/image/upload", {
      method: "post",
      body: data,
    })
      .then((resp) => resp.json())
      .then((data) => {
        setUrl(data.url);
        console.log(data);
      })
      .catch((err) => console.log(err));
  };
  return (
    <div className="ImageUpload mt-5">
      <div className="col-8 m-auto shadow-sm p-0 py-4 border">
        <form className="col-11 m-auto">
          <div className="d-flex">
            <input
              className="form-control"
              type="file"
              id="formFile"
              onChange={(e) => setImage(e.target.files[0])}
            />
            <button className="btn btn-primary col-2" onClick={uploadImage}>
              Upload Image
            </button>
          </div>
          <div className="shadow mt-3 p-3">
            <h4 className="mb-0">File Name : {image}</h4>
          </div>
        </form>
      </div>
      <div className="col-8 m-auto shadow-sm p-0 py-4 border mt-3">
      <div className="col-11 m-auto">
        <h1>Uploaded image will be displayed here</h1>
        <img src={url} />
        </div>
      </div>
    </div>
  );
};
export default App2;
